import { useState , useEffect} from "react";
// node.js library that concatenates classes (strings)
import classnames from "classnames";
// javascipt plugin for creating charts
import Chart from "chart.js";
// react plugin used to create charts
import { Line, Bar } from "react-chartjs-2";
import {MDBBtn} from 'mdb-react-ui-kit'
import {CCloseButton} from '@coreui/react';
import {MDBCloseIcon} from 'mdbreact';
import ScrollBars from 'react-scrollbar';
import { StrictMode } from "react";
import BoxPlot from '../../services/boxplot';  

//import Table from "react-bootstrap/Table"
// import { MDBSmoothScroll } from "mdbreact";

// reactstrap components
import {
  Button,
  Card,
  CardHeader,
  CardBody,
  NavItem,
  NavLink,
  Nav,
  Progress,
  Table,
  Container,
  Row,
  Col,
  CardTitle
} from "reactstrap";

// core components
import { 
  chartOptions,
  parseOptions,
  chartExample1,
  chartExample2,
} from "variables/charts.js";

//import Modal from 'react-modal';
import styled from 'styled-components';
import Header from "components/Headers/Header.js";
// import ApexChart from 'components/modal/barplotModal.js's
import ReactApexChart from 'react-apexcharts';
import parse from 'html-react-parser';
import JsonToTable from 'react-json-to-table';
import ReactHtmlParser from 'react-html-parser'

const Index = (props) => { 
  const [activeNav, setActiveNav] = useState(1);
  const [chartExample1Data, setChartExample1Data] = useState("data1");
  const [openModal , setModalOpen] = useState(false);
  const [tableData, setTableData] = useState('');
  const [fileName, setFileName] = useState('');
  const [geneName, setGeneName] = useState('')
  const [ AD, setAD] = useState([]);
  const [control, setControl] = useState([]);
  const [ADpoints, setAdPoints] = useState(0);
  const [controlPoints, setControlPoinnts] = useState(0);
  
  if (window.Chart) {
    parseOptions(Chart, chartOptions());
  }

useEffect(() => {

   
  var l = JSON.stringify(props.history.location.state)
  const c = props.history.location.state.data ;
  setFileName(props.history.location.state.name)
  console.log(c);

  setTableData(c);
  
   
}, [] )
  const toggleNavs = (e, index) => {
    e.preventDefault();
    setActiveNav(index);
    setChartExample1Data("data" + index);
  };

  const handleClick = (e) => {
    let gene = e.target.innerText;
    console.log( typeof e.target.innerText);
    setGeneName(e.target.innerText)

    BoxPlot.boxPlot(gene, fileName).then(res => {
      console.log(res.data);
    setAD (res.data.Ad_props); //calc
    setControl(res.data.Control_props) 
    setAdPoints(res.data.ad.length)//points
    setControlPoinnts(res.data.control.length)
    console.log(res.data.ad.length)
    setModalOpen(true)
  }).catch((err)=>console.log(err));

    
    
  }

  const closeHandle = () =>{
    setModalOpen(false)
  }


  // const  AD = [54, 66, 69, 75, 88, 90];
  // const control = [54, 59, 66, 71, 88];
  const  series= [
    {
      type: 'boxPlot', 
      data: [
        {
          x: 'AD Sample',
          y: AD
        }, 
        
        {
          x: 'Control Sample',
          y: control
        }
      ]
    
    }] 
  const options = {
    chart: {
      type: 'boxPlot',
      height: 500
    },
    title: {
      text: geneName,
      align: 'left'
    },
    plotOptions: {
      boxPlot: {
        colors: {
          upper: '#28A316',
          lower: '#7BEEB5'
        },
        stroke: {
          width: 0,
          curve: 'smooth'
        },
        fill: {
          opacity: 1,
          type: 'solid'
        },
        legend: {
          position: 'top',
          horizontalAlign: 'left'
        },
        xaxis: {
          type: 'datetime'
        },
      }  
    },
     
  }

   

  
  return (
    <>
      <Header />
      {/* Page content */}

      <Container className="mt--7" fluid>
       
       
               
              
        <Row className="mt-5">
        {openModal? null:
          <Col>
           {/* xl="4"> */}
            <Card className="shadow">
              <CardHeader className="border-0">
                <Row className="align-items-center">
                  <div className="col">
                    <h3 className="mb-0">{fileName}</h3>
                  </div>
                  {/* <div className="col text-right">
                  
                  </div> */}
                </Row>
              </CardHeader>
                  

                <Table style={{ width: '100%',
  height:'100%'}} className="align-items-center table-flush table-hover" responsive onClick={e => handleClick(e)}>
                
                {ReactHtmlParser(tableData)}
               
      
                </Table>
                


 

 
               
            </Card>
          </Col>
        }

          {openModal? 
          
        <>
        {/* <Col>
 <Row> */}

 
         
        <Col className="mb-5 mb-xl-0" xl="8">
          <Card className="shadow">
            <CardHeader className="border-0">
              <Row className="align-items-center">
                <div className="col">
                  <h3 className="mb-0">{geneName}</h3>
                </div>
            
              </Row>
            </CardHeader>
            {/* <Table className="align-items-center table-flush" responsive> */}
              
            <div style={{alignItems:'right'}}>
             <MDBCloseIcon style={{position:'absolute' , left:0, botton:0, top:5, top:5 }} onClick={closeHandle}/>
             </div>
                
               
       <ReactApexChart options={options} series={series} type="boxPlot" height={350} />
              
            {/* </Table> */}
          </Card>
        </Col>
       
        <Col lg="6" xl="3">
 <Row>
                <Card className="card-stats mb-4 mb-xl-0">
                  <CardBody>
                    <Row>
                      <div className="col">
                        <CardTitle
                          tag="h5"
                          className="text-uppercase text-muted mb-0"
                        >
                          AD 
                          Samples
                        </CardTitle>
                        <span className="h2 font-weight-bold mb-0">
                         {ADpoints}
                        </span>
                      </div>
                      <div className="col">
                        <CardTitle
                          tag="h5"
                          className="text-uppercase text-muted mb-0"
                        >
                          Control 
                          Samples
                        </CardTitle>
                        <span className="h2 font-weight-bold mb-0">
                          {controlPoints}
                        </span>
                      </div>
                      
                    </Row>
                    <p className="mt-3 mb-0 text-muted text-sm">
                    {/* <div className="icon icon-shape bg-success text-white rounded-circle shadow">
                          <i className="fas fa-chart-bar" />
                        </div> */}
                      <span className="text-nowrap"> {geneName}</span>
                    </p>
                  </CardBody>
                </Card>
 </Row>

                


              </Col>
   
     
         </>
          :null}
        </Row>
      </Container>

       
    </>
  );

  const scrollBarStyle = {
    border: '1px solid red',
    width: '40px',
    height: '20px',
  };
  

};

// const Table  = styled.table`

// div {
//   overflow-x: auto;
// }
// table {
//   border-collapse: collapse;
//   width: "5rem";
//   Height: "0rem",
// overflow: "auto",
// }
// th {
//   background-color: #f6f9fc;
// }
// th,
// td {
//   padding: 15px;
//   text-align: left;
//   border-bottom: 1px solid #ccc;
// }
// tr:hover {
//   background-color: #ddd;
// }
// tbody{
//   height: '10rem', overflow:'scroll', display: 'block'
// } 
// thead{
//   display: block;
// }
// // align-items: center !important;
// // tbody tr :hover{
// //     background:#ddd
// // }

// th {
//     color: #8898aa;
//     background-color: #f6f9fc;
//     border-color: #e9ecef; }

// // td{
// //     display: flex !important;
// //   align-items: center !important; 
// //   margin-right: 0.5rem !important;}

// span  {
//     margin-right: 0.5rem !important;
// }

//   td,
//   th {
//   vertical-align: middle; }

//    td,
//   th {
//   border-left: 0;
//   border-right: 0; }

//   tbody tr:first-child td,
// tbody tr:first-child th {
//   border-top: 0; }

//  tbody tr:last-child td,
//  tbody tr:last-child th {
//   border-bottom: 0; }

// }
// `;
export default Index;
