import { useState , useEffect} from "react";
// node.js library that concatenates classes (strings)
import classnames from "classnames";
// javascipt plugin for creating charts
import Chart from "chart.js";
// react plugin used to create charts
import { Line, Bar } from "react-chartjs-2";
import {MDBBtn} from 'mdb-react-ui-kit'
import {CCloseButton} from '@coreui/react';
import {MDBCloseIcon} from 'mdbreact';
// reactstrap components
import {
  Button,
  Card,
  CardHeader,
  CardBody,
  NavItem,
  NavLink,
  Nav,
  Progress,
  //Table,
  Container,
  Row,
  Col,
} from "reactstrap";

// core components
import { 
  chartOptions,
  parseOptions,
  chartExample1,
  chartExample2,
} from "variables/charts.js";

//import Modal from 'react-modal';
import styled from 'styled-components';
import Header from "components/Headers/Header.js";
// import ApexChart from 'components/modal/barplotModal.js's
import ReactApexChart from 'react-apexcharts';
import parse from 'html-react-parser';

const Index = (props) => { 
  const [activeNav, setActiveNav] = useState(1);
  const [chartExample1Data, setChartExample1Data] = useState("data1");
  const [openModal , setModalOpen] = useState(false);
  const [tableData, setTableData] = useState({});

  console.log(props.history.location.state.object)
  
  if (window.Chart) {
    parseOptions(Chart, chartOptions());
  }

useEffect(() => {
  console.log(typeof props.history.location.state);
  setTableData(props.history.location.state);
  var ss = document.createElement('div');
  // ss.innerHTML = props.history.location.state;
 
} )
  const toggleNavs = (e, index) => {
    e.preventDefault();
    setActiveNav(index);
    setChartExample1Data("data" + index);
  };

  const handleClick = (e) => {
    let gene = e.target.innerText;
    console.log(e.target.innerText)
    setModalOpen(true)
  }

  const closeHandle = () =>{
    setModalOpen(false)
  }

  const  AD = [54, 66, 69, 75, 88];
  const control = [54, 59, 66, 71, 88];
  const  series= [
    {
      type: 'boxPlot',
      data: [
        {
          x: 'AD Sample',
          y: AD
        },
        
        {
          x: 'Control Sample',
          y: control
        }
      ]
    
    }] 
  const options = {
    chart: {
      type: 'boxPlot',
      height: 250
    },
    title: {
      text: 'Box Plot Chart',
      align: 'left'
    },
    plotOptions: {
      boxPlot: {
        colors: {
          upper: '#28A316',
          lower: '#FEB019'
        },
        stroke: {
          width: 0,
          curve: 'smooth'
        },
        fill: {
          opacity: 1,
          type: 'solid'
        },
        legend: {
          position: 'top',
          horizontalAlign: 'left'
        },
        xaxis: {
          type: 'datetime'
        },
      }  
    }
  }



  
  return (
    <>
      <Header />
      {/* Page content */}
      <Container className="mt--7" fluid>
      
               
              
        <Row className="mt-5">
        {openModal? null:
          <Col>
           {/* xl="4"> */}
            <Card className="shadow">
              <CardHeader className="border-0">
                <Row className="align-items-center">
                  <div className="col">
                    <h3 className="mb-0">Social traffic</h3>
                  </div>
                  <div className="col text-right">
                    <Button
                      color="primary"
                      href="#pablo"
                      onClick={(e) => e.preventDefault()}
                      size="sm"
                    >
                      See all
                    </Button>
                  </div>
                </Row>
              </CardHeader>
              <Table className="align-items-center table-flush table-hover" responsive onClick={e => handleClick(e)}>
                {/* <thead className="thead-light">
                  <tr>
                    <th scope="col">Referral</th>
                    <th scope="col">Visitors</th>
                    <th scope="col" />
                  </tr>
                </thead>
                <tbody>
                  <tr>
                    <th scope="row">Facebook</th>
                    <td>1,480</td>
                    <td>
                      <div className="d-flex align-items-center">
                        <span className="mr-2">60%</span>
                        <div>
                          <Progress
                            max="100"
                            value="60"
                            barClassName="bg-gradient-danger"
                          />
                        </div>
                      </div>
                    </td>
                  </tr>
                  <tr>
                    <th scope="row">Facebook</th>
                    <td>5,480</td>
                    <td>
                      <div className="d-flex align-items-center">
                        <span className="mr-2">70%</span>
                        <div>
                          <Progress
                            max="100"
                            value="70"
                            barClassName="bg-gradient-success"
                          />
                        </div>
                      </div>
                    </td>
                  </tr>
                  <tr>
                    <th scope="row">Google</th>
                    <td>4,807</td>
                    <td>
                      <div className="d-flex align-items-center">
                        <span className="mr-2">80%</span>
                        <div>
                          <Progress max="100" value="80" />
                        </div>
                      </div>
                    </td>
                  </tr>
                  <tr>
                    <th scope="row">Instagram</th>
                    <td>3,678</td>
                    <td>
                      <div className="d-flex align-items-center">
                        <span className="mr-2">75%</span>
                        <div>
                          <Progress
                            max="100"
                            value="75"
                            barClassName="bg-gradient-info"
                          />
                        </div>
                      </div>
                    </td>
                  </tr>
                  <tr>
                    <th scope="row">twitter</th>
                    <td>2,645</td>
                    <td>
                      <div className="d-flex align-items-center">
                        <span className="mr-2">30%</span>
                        <div>
                          <Progress
                            max="100"
                            value="30"
                            barClassName="bg-gradient-warning"
                          />
                        </div>
                      </div>
                    </td>
                  </tr>
                </tbody> */}
                 {/* <tbody> */}
                {Object.keys(tableData).map(function(e){ 
                   return(
                   
                  <div key={e}>
                  {console.log(tableData[e])}
          {/* <tbody> */}
                    <tbody key={e}>
                    {/* <td>{e}</td>
                    <td>{tableData[e]}</td>
                    </tr> */}
                    {tableData[e]}

                    </tbody>

                    </div>
          
                  )
                })}
                  {/* </tbody>/ */}
                {/* {parse(tableData)} */}
                </Table>
            </Card>
          </Col>
        }

          {openModal?
          <Col className="mb-5 mb-xl-0" xl="8">
            <Card className="shadow">
              <CardHeader className="border-0">
              <div style={{alignItems:'right'}}>
              <MDBCloseIcon style={{position:'absolute' , left:0, botton:0, top:5, top:5 }} onClick={closeHandle}/>
              </div>
                
               
      <ReactApexChart options={options} series={series} type="boxPlot" height={350} />
              
                
         
              </CardHeader>
              
            </Card>
          </Col>
          :null}
        </Row>
      </Container>

       
    </>
  );
};

const Table  = styled.table`

align-items: center !important;
tbody tr :hover{
    background:#ddd
}

th {
    color: #8898aa;
    background-color: #f6f9fc;
    border-color: #e9ecef; }

td{
    display: flex !important;
  align-items: center !important; 
  margin-right: 0.5rem !important;}

span  {
    margin-right: 0.5rem !important;
}

  td,
  th {
  vertical-align: middle; }

   td,
  th {
  border-left: 0;
  border-right: 0; }

  tbody tr:first-child td,
tbody tr:first-child th {
  border-top: 0; }

 tbody tr:last-child td,
 tbody tr:last-child th {
  border-bottom: 0; }

}
`;
export default Index;
