import { useState , useEffect} from "react";
// node.js library that concatenates classes (strings)
import classnames from "classnames";
// javascipt plugin for creating charts
import Chart from "chart.js";
// react plugin used to create charts
import { Line, Bar } from "react-chartjs-2";
import {MDBBtn} from 'mdb-react-ui-kit'
import {CCloseButton} from '@coreui/react';
import {MDBCloseIcon} from 'mdbreact';
import ScrollBars from 'react-scrollbar';
import { StrictMode } from "react";
import BoxPlot from '../../services/boxplot';  

//import Table from "react-bootstrap/Table"
// import { MDBSmoothScroll } from "mdbreact";

// reactstrap components
import {
  Button,
  Card,
  CardHeader,
  CardBody,
  NavItem,
  NavLink,
  Nav,
  Progress,
  Table,
  Container,
  Row,
  Col,
} from "reactstrap";

// core components
import { 
  chartOptions,
  parseOptions,
  chartExample1,
  chartExample2,
} from "variables/charts.js";

//import Modal from 'react-modal';
import styled from 'styled-components';
import Header from "components/Headers/Header.js";
// import ApexChart from 'components/modal/barplotModal.js's
import ReactApexChart from 'react-apexcharts';
import parse from 'html-react-parser';
import JsonToTable from 'react-json-to-table';
import ReactHtmlParser from 'react-html-parser'

const Index = (props) => { 
  const [activeNav, setActiveNav] = useState(1);
  const [chartExample1Data, setChartExample1Data] = useState("data1");
  const [openModal , setModalOpen] = useState(false);
  const [tableData, setTableData] = useState('');
  const [fileName, setFileName] = useState('');
  const [geneName, setGeneName] = useState('')
  const [ AD, setAD] = useState([]);
  const [control, setControl] = useState([]);
  const [ADpoints, setAdPoints] = useState();
  const [controlPoints, setControlPoinnts] = useState();
  
  if (window.Chart) {
    parseOptions(Chart, chartOptions());
  }

useEffect(() => {

   
  var l = JSON.stringify(props.history.location.state)
  const c = props.history.location.state.data ;
  setFileName(props.history.location.state.name)
  console.log(c);

  setTableData(c);
  
   
}, [] )
  const toggleNavs = (e, index) => {
    e.preventDefault();
    setActiveNav(index);
    setChartExample1Data("data" + index);
  };

  const handleClick = (e) => {
    let gene = e.target.innerText;
    console.log( typeof e.target.innerText);
    setGeneName(e.target.innerText)

    BoxPlot.boxPlot(gene, fileName).then(res => {
      console.log(res.data);
    setAD (res.data.Ad_props); //calc
    setControl(res.data.Control_props) 
    setAdPoints(res.data.control.length)//points
    setControlPoinnts(res.data.ad.length)
    console.log(controlPoints)
    setModalOpen(true)
  }).catch((err)=>console.log(err));

    
    
  }

  const closeHandle = () =>{
    setModalOpen(false)
  }


  // const  AD = [54, 66, 69, 75, 88, 90];
  // const control = [54, 59, 66, 71, 88];
  const  series= [
    {
      type: 'boxPlot', 
      data: [
        {
          x: 'AD Sample',
          y: AD
        }, 
        
        {
          x: 'Control Sample',
          y: control
        }
      ]
    
    }] 
  const options = {
    chart: {
      type: 'boxPlot',
      height: 500
    },
    title: {
      text: geneName,
      align: 'left'
    },
    plotOptions: {
      boxPlot: {
        colors: {
          upper: '#28A316',
          lower: '#7BEEB5'
        },
        stroke: {
          width: 0,
          curve: 'smooth'
        },
        fill: {
          opacity: 1,
          type: 'solid'
        },
        legend: {
          position: 'top',
          horizontalAlign: 'left'
        },
        xaxis: {
          type: 'datetime'
        },
      }  
    },
     
  }

   

  
  return (
    <>
      <Header />
      {/* Page content */}

      <Container className="mt--7" fluid>

      
       
               
              
        <Row className="mt-5">
        {openModal? null:
          <Col>
           {/* xl="4"> */}
            <Card className="shadow">
              <CardHeader className="border-0">
                <Row className="align-items-center">
                  <div className="col">
                    <h3 className="mb-0">{fileName}</h3>
                  </div>
                  {/* <div className="col text-right">
                  
                  </div> */}
                </Row>
              </CardHeader>
                  

                <Table style={{ width: '100%',
  height:'100%'}} className="align-items-center table-flush table-hover" responsive onClick={e => handleClick(e)}>
                
                {ReactHtmlParser(tableData)}
               
      
                </Table>
                


 

 
               
            </Card>
          </Col>
        }

          {openModal? 
          
        <>
        <Col className="mb-5 mb-xl-0" xl="8">
          <Card className="shadow">
            <CardHeader className="border-0">
              <Row className="align-items-center">
                <div className="col">
                  <h3 className="mb-0">{geneName}</h3>
                </div>
            
              </Row>
            </CardHeader>
            {/* <Table className="align-items-center table-flush" responsive> */}
              
            <div style={{alignItems:'right'}}>
             <MDBCloseIcon style={{position:'absolute' , left:0, botton:0, top:5, top:5 }} onClick={closeHandle}/>
             </div>
                
               
       <ReactApexChart options={options} series={series} type="boxPlot" height={350} />
              
            {/* </Table> */}
          </Card>
        </Col>
