/// reactstrap components
import {
  Button,
  Card,
  CardHeader,
  CardBody,
  FormGroup,
  Form,
  Input,
  InputGroupAddon,
  InputGroupText,
  InputGroup,
  Row,
  Col,
} from "reactstrap";
import React, {useState, useEffect, useContext} from 'react';
import {Redirect, useHistory} from 'react-router-dom';
const Login = () => {


  let history = useHistory();
    const [ user, setUser] = useState({});
    const [error, setErrors] = useState({});
    const [submit, setSubmit] = useState(false);

    const handleChange = e => { 
        setUser({...user, [e.target.name]:e.target.value});
    };

    const handleSubmit = e => {
        e.preventDefault();
        setErrors(validate(user));
        setSubmit(true);

    };

    useEffect(()=>{
        let errorx = {}
        if(Object.keys(error).length === 0 && submit){
            console.log(user);
            let email = user.email;
            let password = user.password;
            authService.login(email, password)
            .then(res => {console.log(res)
                //console.log(res);

            if(res.data.message)     //INVALID LOGIN
            errorx.all = 'Invalid User!, Email or Password is incorrect.'; 

            else if(res.data.email)
            errorx.all = 'Please confirm your email to login.'; 


            else{   //LOGGED IN
            localStorage.setItem('isLoggedIn', true);

            errorx.all = 'Success';
            console.log(state, 'loggin');
            dispatch({type:'USER', payload:true});
            console.log(state, 'loggin');
           // <Redirect to='/'/>;
           history.push('/home');

            } 
    setErrors(errorx); 
            })

            .catch(err=>{console.log(err)});
        }
    }, [error]);


  return (
    <>
      <Col lg="5" md="10">
        <Card className="bg-secondary shadow border-0">
          <CardHeader className="bg-transparent pb-5">
            <div className="text-muted text-center mt-1 mb-2">
              <small>Sign in with</small>
            </div>
            <div className="btn-wrapper text-center">

              <Button
                className="btn-neutral btn-icon"
                color="default"
                href="#pablo"
                onClick={(e) => e.preventDefault()}
              >
                <span className="btn-inner--icon">
                  <img
                    alt="..."
                    src={
                      require("../../assets/img/icons/common/google.svg")
                        .default
                    }
                  />
                </span>
                <span className="btn-inner--text">Google</span>
              </Button>
            </div>
          </CardHeader>
          <CardBody className="px-lg-5 py-lg-4">
            <div className="text-center text-muted mb-4">
              <small>Or sign in with credentials</small>
            </div>
            <Form role="form">
              <FormGroup className="mb-3">
                <InputGroup className="input-group-alternative">
                  <InputGroupAddon addonType="prepend">
                    <InputGroupText>
                      <i className="ni ni-email-83" />
                    </InputGroupText>
                  </InputGroupAddon>
                  <Input
                    placeholder="Email"
                    type="email"
                    autoComplete="new-email"
                  />
                </InputGroup>
              </FormGroup>
              <FormGroup>
                <InputGroup className="input-group-alternative">
                  <InputGroupAddon addonType="prepend">
                    <InputGroupText>
                      <i className="ni ni-lock-circle-open" />
                    </InputGroupText>
                  </InputGroupAddon>
                  <Input
                    placeholder="Password"
                    type="password"
                    autoComplete="new-password"
                  />
                </InputGroup>
              </FormGroup>
              <div className="custom-control custom-control-alternative custom-checkbox">
                <input
                  className="custom-control-input"
                  id=" customCheckLogin"
                  type="checkbox"
                />
                <label
                  className="custom-control-label"
                  htmlFor=" customCheckLogin"
                >
                  <span className="text-muted">Remember me</span>
                </label>
              </div>
              <div className="text-center">
                <Button className="my-4" color="primary" type="button">
                  Sign in
                </Button>
              </div>
            </Form>
          </CardBody>
        </Card>
        <Row className="mt-3">
          <Col xs="6">
            <a
              className="text-light"
              href="#pablo"
              onClick={(e) => e.preventDefault()}
            >
              <small>Forgot password?</small>
            </a>
          </Col>
          <Col className="text-right" xs="6">
            <a
              className="text-light"
              href="#pablo"
              onClick={(e) => e.preventDefault()}
            >
              <small>Create new account</small>
            </a>
          </Col>
        </Row>
      </Col>
    </>
  );
};

export default Login;
