import { useState , useEffect} from "react";
// node.js library that concatenates classes (strings)
import classnames from "classnames";
// javascipt plugin for creating charts
import Chart from "chart.js";
// react plugin used to create charts
import { Line, Bar } from "react-chartjs-2";
import {MDBBtn} from 'mdb-react-ui-kit'
import {CCloseButton} from '@coreui/react';
import {MDBCloseIcon} from 'mdbreact';
import ScrollBars from 'react-scrollbar';
import { StrictMode } from "react";
import BoxPlot from '../../services/boxplot';  

//import Table from "react-bootstrap/Table"
// import { MDBSmoothScroll } from "mdbreact";

// reactstrap components
import {
  Button,
  Card,
  CardHeader,
  CardBody,
  NavItem,
  NavLink,
  Nav,
  Progress,
  Table,
  Container,
  Row,
  Col,
} from "reactstrap";

// core components
import { 
  chartOptions,
  parseOptions,
  chartExample1,
  chartExample2,
} from "variables/charts.js";

//import Modal from 'react-modal';
import styled from 'styled-components';
import Header from "components/Headers/Header.js";
// import ApexChart from 'components/modal/barplotModal.js's
import ReactApexChart from 'react-apexcharts';
import parse from 'html-react-parser';
import JsonToTable from 'react-json-to-table';
import ReactHtmlParser from 'react-html-parser'

const Index = (props) => { 
  const [activeNav, setActiveNav] = useState(1);
  const [chartExample1Data, setChartExample1Data] = useState("data1");
  const [openModal , setModalOpen] = useState(false);
  const [tableData, setTableData] = useState('');
  const [fileName, setFileName] = useState('');
  const [geneName, setGeneName] = useState('')
  const [ AD, setAD] = useState([]);
  const [control, setControl] = useState([]);
  
  if (window.Chart) {
    parseOptions(Chart, chartOptions());
  }

useEffect(() => {

   
  var l = JSON.stringify(props.history.location.state)
  const c = props.history.location.state.data ;
  setFileName(props.history.location.state.name)
  console.log(c);

  setTableData(c);
  
   
}, [] )
  const toggleNavs = (e, index) => {
    e.preventDefault();
    setActiveNav(index);
    setChartExample1Data("data" + index);
  };

  const handleClick = (e) => {
    let gene = e.target.innerText;
    console.log( typeof e.target.innerText);
    setGeneName(e.target.innerText)

    BoxPlot.boxPlot(gene, fileName).then(res => {
      console.log(res.data);
    setAD (res.data.Ad_props);
    setControl(res.data.Control_props)
    setModalOpen(true)
  }).catch((err)=>console.log(err));

    
    
  }

  const closeHandle = () =>{
    setModalOpen(false)
  }


  // const  AD = [54, 66, 69, 75, 88, 90];
  // const control = [54, 59, 66, 71, 88];
  const  series= [
    {
      type: 'boxPlot', 
      data: [
        {
          x: 'AD Sample',
          y: AD
        }, 
        
        {
          x: 'Control Sample',
          y: control
        }
      ]
    
    }] 
  const options = {
    chart: {
      type: 'boxPlot',
      height: 500
    },
    title: {
      text: geneName,
      align: 'left'
    },
    plotOptions: {
      boxPlot: {
        colors: {
          upper: '#28A316',
          lower: '#7BEEB5'
        },
        stroke: {
          width: 0,
          curve: 'smooth'
        },
        fill: {
          opacity: 1,
          type: 'solid'
        },
        legend: {
          position: 'top',
          horizontalAlign: 'left'
        },
        xaxis: {
          type: 'datetime'
        },
      }  
    },
     
  }

   

  
  return (
    <>
      <Header />
      {/* Page content */}

      <Container className="mt--7" fluid>

      
       
               
              
        <Row className="mt-5">
        {openModal? null:
          <Col>
           {/* xl="4"> */}
            <Card className="shadow">
              <CardHeader className="border-0">
                <Row className="align-items-center">
                  <div className="col">
                    <h3 className="mb-0">{fileName}</h3>
                  </div>
                  {/* <div className="col text-right">
                  
                  </div> */}
                </Row>
              </CardHeader>
                  

                <Table style={{ width: '100%',
  height:'100%'}} className="align-items-center table-flush table-hover" responsive onClick={e => handleClick(e)}>
                
                {ReactHtmlParser(tableData)}
               
      
                </Table>
                


 

 
               
            </Card>
          </Col>
        }

          {openModal? 
          
        <Row className="mt-5">
        <Col className="mb-5 mb-xl-0" xl="8">
          <Card className="shadow">
            <CardHeader className="border-0">
              <Row className="align-items-center">
                <div className="col">
                  <h3 className="mb-0">Page visits</h3>
                </div>
                <div className="col text-right">
                  <Button
                    color="primary"
                    href="#pablo"
                    onClick={(e) => e.preventDefault()}
                    size="sm"
                  >
                    See all
                  </Button>
                </div>
              </Row>
            </CardHeader>
            <Table className="align-items-center table-flush" responsive>
              <thead className="thead-light">
                <tr>
                  <th scope="col">Page name</th>
                  <th scope="col">Visitors</th>
                  <th scope="col">Unique users</th>
                  <th scope="col">Bounce rate</th>
                </tr>
              </thead>
              <tbody>
                <tr>
                  <th scope="row">/argon/</th>
                  <td>4,569</td>
                  <td>340</td>
                  <td>
                    <i className="fas fa-arrow-up text-success mr-3" /> 46,53%
                  </td>
                </tr>
                <tr>
                  <th scope="row">/argon/index.html</th>
                  <td>3,985</td>
                  <td>319</td>
                  <td>
                    <i className="fas fa-arrow-down text-warning mr-3" />{" "}
                    46,53%
                  </td>
                </tr>
                <tr>
                  <th scope="row">/argon/charts.html</th>
                  <td>3,513</td>
                  <td>294</td>
                  <td>
                    <i className="fas fa-arrow-down text-warning mr-3" />{" "}
                    36,49%
                  </td>
                </tr>
                <tr>
                  <th scope="row">/argon/tables.html</th>
                  <td>2,050</td>
                  <td>147</td>
                  <td>
                    <i className="fas fa-arrow-up text-success mr-3" /> 50,87%
                  </td>
                </tr>
                <tr>
                  <th scope="row">/argon/profile.html</th>
                  <td>1,795</td>
                  <td>190</td>
                  <td>
                    <i className="fas fa-arrow-down text-danger mr-3" />{" "}
                    46,53%
                  </td>
                </tr>
              </tbody>
            </Table>
          </Card>
        </Col>
        <Col xl="4">
          <Card className="shadow">
            <CardHeader className="border-0">
              <Row className="align-items-center">
                <div className="col">
                  <h3 className="mb-0">Social traffic</h3>
                </div>
                <div className="col text-right">
                  <Button
                    color="primary"
                    href="#pablo"
                    onClick={(e) => e.preventDefault()}
                    size="sm"
                  >
                    See all
                  </Button>
                </div>
              </Row>
            </CardHeader>
            <Table className="align-items-center table-flush" responsive>
              <thead className="thead-light">
                <tr>
                  <th scope="col">Referral</th>
                  <th scope="col">Visitors</th>
                  <th scope="col" />
                </tr>
              </thead>
              <tbody>
                <tr>
                  <th scope="row">Facebook</th>
                  <td>1,480</td>
                  <td>
                    <div className="d-flex align-items-center">
                      <span className="mr-2">60%</span>
                      <div>
                        <Progress
                          max="100"
                          value="60"
                          barClassName="bg-gradient-danger"
                        />
                      </div>
                    </div>
                  </td>
                </tr>
                <tr>
                  <th scope="row">Facebook</th>
                  <td>5,480</td>
                  <td>
                    <div className="d-flex align-items-center">
                      <span className="mr-2">70%</span>
                      <div>
                        <Progress
                          max="100"
                          value="70"
                          barClassName="bg-gradient-success"
                        />
                      </div>
                    </div>
                  </td>
                </tr>
                <tr>
                  <th scope="row">Google</th>
                  <td>4,807</td>
                  <td>
                    <div className="d-flex align-items-center">
                      <span className="mr-2">80%</span>
                      <div>
                        <Progress max="100" value="80" />
                      </div>
                    </div>
                  </td>
                </tr>
                <tr>
                  <th scope="row">Instagram</th>
                  <td>3,678</td>
                  <td>
                    <div className="d-flex align-items-center">
                      <span className="mr-2">75%</span>
                      <div>
                        <Progress
                          max="100"
                          value="75"
                          barClassName="bg-gradient-info"
                        />
                      </div>
                    </div>
                  </td>
                </tr>
                <tr>
                  <th scope="row">twitter</th>
                  <td>2,645</td>
                  <td>
                    <div className="d-flex align-items-center">
                      <span className="mr-2">30%</span>
                      <div>
                        <Progress
                          max="100"
                          value="30"
                          barClassName="bg-gradient-warning"
                        />
                      </div>
                    </div>
                  </td>
                </tr>
              </tbody>
            </Table>
          </Card>
        </Col>
      </Row>
      //     <Col className="mb-5 mb-xl-0" xl="8">
      //       <Card className="shadow">
      //         <CardHeader className="border-0">
      //         <div style={{alignItems:'right'}}>
      //         <MDBCloseIcon style={{position:'absolute' , left:0, botton:0, top:5, top:5 }} onClick={closeHandle}/>
      //         </div>
                
               
      // <ReactApexChart options={options} series={series} type="boxPlot" height={350} />
              
                
         
      //         </CardHeader>
              
      //       </Card>
      //     </Col>
         
          :null}
        </Row>
      </Container>

       
    </>
  );

  const scrollBarStyle = {
    border: '1px solid red',
    width: '40px',
    height: '20px',
  };
  

};

